import React from 'react';
import UpdateStoreForm from './components/UpdateStoreForm';
import DeleteStore from './components/DeleteStore';
import StoreDetails from './components/StoreDetails';
import CreateStoreForm from './components/CreateStoreForm';
import './styles/App.css';

function App() {
  return (
    <div className="app-container">
      <header className="app-header">
        <h1 className="app-title">Store Management System</h1>
        <p className="app-subtitle">Manage your stores with ease and efficiency</p>
      </header>

      <div className="components-grid">
        <div className="component-card">
          <CreateStoreForm />
        </div>

        <div className="component-card">
          <StoreDetails />
        </div>

        <div className="component-card">
          <UpdateStoreForm />
        </div>

        <div className="component-card">
          <DeleteStore />
        </div>
      </div>
    </div>
  );
}

export default App;
