import React, { useState } from 'react';
import { createStore } from '../api/storeService';

function CreateStoreForm() {
  const [storeData, setStoreData] = useState({ 
    storeName: '', 
    address: '', 
    managerName: '' 
  });

  const handleChange = (e) => {
    setStoreData({ ...storeData, [e.target.name]: e.target.value });
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    try {
      console.log('Sending store data:', storeData);
      const response = await createStore(storeData);
      console.log('Store created successfully:', response.data);
      alert('Store created successfully!');
      // Reset form after successful creation
      setStoreData({ storeName: '', address: '', managerName: '' });
    } catch (error) {
      console.error('Full error object:', error);
      console.error('Error response:', error.response);
      console.error('Error message:', error.message);

      if (error.response) {
        // Server responded with error status
        alert(`Error creating store: ${error.response.data.message || error.response.statusText}`);
      } else if (error.request) {
        // Request was made but no response received
        alert('Error: Unable to connect to server. Please check if the server is running.');
      } else {
        // Something else happened
        alert('Error creating store. Please try again.');
      }
    }
  };

  return (
    <form onSubmit={handleSubmit}>
      <h2>Create Store</h2>
      <input 
        name="storeName" 
        placeholder="Store Name" 
        value={storeData.storeName}
        onChange={handleChange}
        required 
      />
      <input 
        name="address" 
        placeholder="Address" 
        value={storeData.address}
        onChange={handleChange}
        required 
      />
      <input 
        name="managerName" 
        placeholder="Manager Name" 
        value={storeData.managerName}
        onChange={handleChange}
        required 
      />
      <button type="submit">Create Store</button>
    </form>
  );
}

export default CreateStoreForm;
