import React, { useState } from 'react';
import { getStore } from '../api/storeService';

function StoreDetails() {
  const [storeId, setStoreId] = useState('');
  const [store, setStore] = useState(null);
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState('');

  const handleFetch = async () => {
    if (!storeId.trim()) {
      setError('Please enter a store ID');
      return;
    }

    setLoading(true);
    setError('');
    try {
      const response = await getStore(storeId);
      setStore(response.data);
      console.log('Store data received:', response.data);
    } catch (error) {
      console.error('Error fetching store:', error);
      if (error.response) {
        setError(`Error: ${error.response.data.message || error.response.statusText}`);
      } else if (error.request) {
        setError('Error: Unable to connect to server. Please check if the server is running.');
      } else {
        setError('Error fetching store details. Please try again.');
      }
      setStore(null);
    } finally {
      setLoading(false);
    }
  };

  return (
    <div>
      <h2>Store Details</h2>
      <input 
        value={storeId} 
        onChange={(e) => setStoreId(e.target.value)} 
        placeholder="Store ID" 
      />
      <button onClick={handleFetch} disabled={loading}>
        {loading ? 'Fetching...' : 'Fetch'}
      </button>

      {error && (
        <div style={{ color: 'red', marginTop: '10px' }}>
          {error}
        </div>
      )}

      {store && (
        <div style={{ marginTop: '20px', padding: '10px', border: '1px solid #ccc' }}>
          <h3>Store Information</h3>
          <p><strong>ID:</strong> {store.id}</p>
          <p><strong>Store Name:</strong> {store.storeName}</p>
          <p><strong>Address:</strong> {store.address}</p>
          <p><strong>Manager Name:</strong> {store.managerName}</p>
        </div>
      )}
    </div>
  );
}

export default StoreDetails;
