import React, { useState } from 'react';
import { createStore } from '../api/storeService';

function CreateStoreForm() {
  const [storeData, setStoreData] = useState({ 
    storeName: '', 
    address: '', 
    managerName: '' 
  });
  const [loading, setLoading] = useState(false);

  const handleChange = (e) => {
    setStoreData({ ...storeData, [e.target.name]: e.target.value });
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    setLoading(true);

    try {
      const dataToSend = {
        storeName: storeData.storeName,
        address: storeData.address,
        managerName: storeData.managerName
      };

      const response = await createStore(dataToSend);
      alert('üéâ Store created successfully!');
      setStoreData({ storeName: '', address: '', managerName: '' });
    } catch (error) {
      console.error('Error creating store:', error);
      alert('‚ùå Error creating store. Please try again.');
    } finally {
      setLoading(false);
    }
  };

  return (
    <div>
      <h2 className="component-title">
        <i className="fas fa-plus-circle icon"></i>
        Create New Store
      </h2>
      <form onSubmit={handleSubmit}>
        <div className="form-group">
          <input 
            className="form-input"
            name="storeName" 
            placeholder="üè™ Store Name" 
            value={storeData.storeName}
            onChange={handleChange}
            required 
          />
        </div>

        <div className="form-group">
          <input 
            className="form-input"
            name="address" 
            placeholder="üìç Address" 
            value={storeData.address}
            onChange={handleChange}
            required 
          />
        </div>

        <div className="form-group">
          <input 
            className="form-input"
            name="managerName" 
            placeholder="üë§ Manager Name" 
            value={storeData.managerName}
            onChange={handleChange}
            required 
          />
        </div>

        <button 
          type="submit" 
          className="btn btn-primary"
          disabled={loading}
          style={{ width: '100%' }}
        >
          {loading ? (
            <>
              <span className="loading-spinner"></span>
              Creating...
            </>
          ) : (
            <>
              <i className="fas fa-rocket icon"></i>
              Create Store
            </>
          )}
        </button>
      </form>
    </div>
  );
}

export default CreateStoreForm;
