import React, { useState } from 'react';
import { deleteStore } from '../api/storeService';

function DeleteStore() {
  const [storeId, setStoreId] = useState('');
  const [loading, setLoading] = useState(false);

  const handleDelete = async () => {
    if (!storeId.trim()) {
      alert('Please enter a store ID');
      return;
    }

    if (!window.confirm('Are you sure you want to delete this store? This action cannot be undone.')) {
      return;
    }

    setLoading(true);
    try {
      await deleteStore(storeId);
      alert('Store deleted successfully!');
      setStoreId('');
    } catch (error) {
      console.error('Error deleting store:', error);
      if (error.response) {
        alert(`Error deleting store: ${error.response.data.message || error.response.statusText}`);
      } else if (error.request) {
        alert('Error: Unable to connect to server. Please check if the server is running.');
      } else {
        alert('Error deleting store. Please try again.');
      }
    } finally {
      setLoading(false);
    }
  };

  return (
    <div>
      <h2 className="component-title">Delete Store</h2>

      <div className="form-group">
        <input 
          className="form-input"
          value={storeId} 
          onChange={(e) => setStoreId(e.target.value)} 
          placeholder="Enter Store ID to Delete" 
        />
      </div>

      <button 
        onClick={handleDelete}
        disabled={loading}
        className="btn btn-danger"
        style={{ width: '100%' }}
      >
        {loading ? 'Deleting...' : 'Delete Store'}
      </button>
    </div>
  );
}

export default DeleteStore;
