import React, { useState } from 'react';
import { deleteStore } from '../api/storeService';

function DeleteStore() {
  const [storeId, setStoreId] = useState('');
  const [loading, setLoading] = useState(false);

  const handleDelete = async () => {
    if (!storeId.trim()) {
      alert('Please enter a store ID');
      return;
    }

    if (!window.confirm('‚ö†Ô∏è Are you sure you want to delete this store? This action cannot be undone.')) {
      return;
    }

    setLoading(true);
    try {
      await deleteStore(storeId);
      alert('üóëÔ∏è Store deleted successfully!');
      setStoreId('');
    } catch (error) {
      alert('‚ùå Error deleting store. Please try again.');
    } finally {
      setLoading(false);
    }
  };

  return (
    <div>
      <h2 className="component-title">
        <i className="fas fa-trash-alt icon"></i>
        Delete Store
      </h2>

      <div className="form-group">
        <input 
          className="form-input"
          value={storeId} 
          onChange={(e) => setStoreId(e.target.value)} 
          placeholder="üóëÔ∏è Enter Store ID to Delete" 
        />
      </div>

      <button 
        onClick={handleDelete}
        disabled={loading}
        className="btn btn-danger"
        style={{ width: '100%' }}
      >
        {loading ? (
          <>
            <span className="loading-spinner"></span>
            Deleting...
          </>
        ) : (
          <>
            <i className="fas fa-fire icon"></i>
            Delete Store
          </>
        )}
      </button>
    </div>
  );
}

export default DeleteStore;
