import React, { useState } from 'react';
import { updateStore } from '../api/storeService';

function UpdateStoreForm() {
  const [storeData, setStoreData] = useState({ id: '', name: '', location: '' });

  const handleChange = (e) => {
    setStoreData({ ...storeData, [e.target.name]: e.target.value });
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    await updateStore(storeData);
    alert('Store updated successfully!');
  };

  return (
    <form onSubmit={handleSubmit}>
      <h2>Update Store</h2>
      <input name="id" placeholder="Store ID" onChange={handleChange} />
      <input name="name" placeholder="Name" onChange={handleChange} />
      <input name="location" placeholder="Location" onChange={handleChange} />
      <button type="submit">Update</button>
    </form>
  );
}

export default UpdateStoreForm;
