import React, { useState } from 'react';
import { createStore } from '../api/storeService';

function CreateStoreForm() {
  const [storeData, setStoreData] = useState({ 
    storeName: '', 
    address: '', 
    managerName: '' 
  });
  const [loading, setLoading] = useState(false);

  const handleChange = (e) => {
    setStoreData({ ...storeData, [e.target.name]: e.target.value });
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    setLoading(true);

    try {
      const dataToSend = {
        storeName: storeData.storeName,
        address: storeData.address,
        managerName: storeData.managerName
      };

      console.log('Sending store data:', dataToSend);
      const response = await createStore(dataToSend);
      console.log('Store created successfully:', response.data);
      alert('Store created successfully!');
      setStoreData({ storeName: '', address: '', managerName: '' });
    } catch (error) {
      console.error('Full error object:', error);

      if (error.response) {
        alert(`Error creating store: ${error.response.data.message || error.response.statusText}`);
      } else if (error.request) {
        alert('Error: Unable to connect to server. Please check if the server is running on port 8081.');
      } else {
        alert('Error creating store. Please try again.');
      }
    } finally {
      setLoading(false);
    }
  };

  return (
    <div>
      <h2 className="component-title">Create New Store</h2>
      <form onSubmit={handleSubmit}>
        <div className="form-group">
          <input 
            className="form-input"
            name="storeName" 
            placeholder="Store Name" 
            value={storeData.storeName}
            onChange={handleChange}
            required 
          />
        </div>

        <div className="form-group">
          <input 
            className="form-input"
            name="address" 
            placeholder="Address" 
            value={storeData.address}
            onChange={handleChange}
            required 
          />
        </div>

        <div className="form-group">
          <input 
            className="form-input"
            name="managerName" 
            placeholder="Manager Name" 
            value={storeData.managerName}
            onChange={handleChange}
            required 
          />
        </div>

        <button 
          type="submit" 
          className="btn btn-primary"
          disabled={loading}
          style={{ width: '100%' }}
        >
          {loading ? 'Creating...' : 'Create Store'}
        </button>
      </form>
    </div>
  );
}

export default CreateStoreForm;
